eval(JSON.parse(fetch('hiker://page/lazy', {})).rule);
let config = [{
    "name": "酷我音乐",
    "url": "https://cfyy.cc/mp3/kw.php?ss=**"
}, {
    "name": "酷狗音乐",
    "url": "https://cfyy.cc/mp3/kg.php?ss=**"
}, {
    "name": "网易音乐",
    "url": "https://cfyy.cc/mp3/163.php?ss=**"
}, {
    "name": "MV",
    "url": "https://cfyy.cc/mp4/kg/cf.php?ss=**"

}]
let rules = config.map((source) => {
    return {
        title: source.name,
        search_url: 'hiker://empty?key=**&p=fypage',
        searchFind: 'js:' + $.toString((name, url, lazy) => {
            let d = [];
            key = getParam('key');
            var url = url.replace(/\*\*/, key);
            if (name === 'MV') {
                var list = pdfa(request(url, {}), 'body&&.jc')
                list.forEach(data => {
                    d.push({
                        title: pdfh(data, 'a&&title'),
                        // desc: data.author,
                        img: pdfh(data, 'img&&src') + '@Referer=',
                        url: 'https://cfyy.cc/mp4/kg/' + pdfh(data, 'a&&href') + lazy,
                        col_type: '',
                    })
                })
            } else {
                var list = JSON.parse(request(url, {})).Data
                try {
                    list.forEach(data => {
                        var pic = data.pic
                        if (!/http/.test(pic)) {
                            var pic = 'https:' + pic
                        }
                        d.push({
                            title: data.title,
                            desc: data.author,
                            img: pic + '@Referer=',
                            url: data.url + $("").lazyRule((lrc) => {
                                return JSON.stringify({
                                    urls: [input],
                                    lyric: lrc
                                })
                            }, data.lrc),
                            col_type: '',
                        })
                    })
                } catch (e) {}
            }
            setResult(d);
        }, source.name, source.url, lazy)
    };
})
setResult([{
    title: "点我开始聚搜",
    url: "hiker://search?s=" + getParam("key"),
    extra: {
        rules: $.toString((rules) => {
            return rules
        }, rules)
    }
}])

