		undefined
		var date = new Date(tm * 1000); //时间戳为10位需*1000，时间戳为13位的话不需乘1000
		var Y = date.getFullYear() + '-';
		var M = (date.getMonth() + 1 < 10 ? '0' + (date.getMonth() + 1) : date.getMonth() + 1) + '-';
		var D = date.getDate();
		var h = ' | ' + date.getHours() + ':';
		var m = date.getMinutes();
		if(m<10) m = '0'+m;
		m = m + ':'
		var s = date.getSeconds();
		if(s<10) s = '0'+s;
		if (ts == 0) return Y + M + D;
		if (ts == 1) return Y + M + D + h + m + s;
	}"},{"col_type":"movie_3","name":"app_二级列表","path":"app_二级列表","rule":"$('hiker://empty#fypage##noHistory##autoCache##noRecordHistory##immersiveTheme##noRefresh##noLoading#').rule((input) => {
