var d = [];
let json = {};
try {
    let j = fetch("https://studio-api.suno.ai/api/playlist/1190bf92-10dc-4ce5-968a-7a377f37f984/?page=1", {
        timeout: 10000,
        headers: {
            Referer: "https://app.suno.ai/"
        }
    });
    //log(j);
    json = JSON.parse(j);
    if (json && json.playlist_clips && json.playlist_clips.length > 0) {
        for (let it of json.playlist_clips) {
            //log(it);
            d.push({
                title: it.clip.title + "\n\n" + it.clip.play_count + "播放",
                url: it.clip.audio_url,
                col_type: "card_pic_1",
                desc: "0",
                pic_url: it.clip.image_url
            });
        }
    }
} catch (e) {
    log(e.toString());
}

if (d.length <= 0) {
    let html = fetch("https://haikuoshijie.cn/suno-api");
    let arr = pdfa(html, ".grid&&.bg-card");
    for (let it of arr) {
        d.push({
            title: pdfh(it, "h2&&Text") + "\n\n" + pdfh(it, ".grid-cols-2&&.text-sm,1&&Text") + "播放",
            url: pd(it, "audio&&src"),
            col_type: "card_pic_1",
            desc: "0",
            pic_url: pd(it, "img&&src")
        });
    }
}

setResult(d);

